// SPDX-License-Identifier: MIT
pragma solidity ^0.8.20;

/**
 * @notice We are importing two standard, secure contracts from the OpenZeppelin library.
 * ERC20.sol is the template for a standard token.
 * Ownable.sol gives us tools to manage contract ownership.
 */
import "@openzeppelin/contracts/token/ERC20/ERC20.sol";
import "@openzeppelin/contracts/access/Ownable.sol";

/**
 * @title GameToken
 * @dev An ERC-20 token with 18 decimals.
 * The key rule is that only the owner of this contract can create (mint) new tokens.
 * This is required so that only our TokenStore can issue new GT.
 */
contract GameToken is ERC20, Ownable {
    /**
     * @dev The constructor is a special function that runs only once when the contract is deployed.
     * Here, we are setting the token's name to "GameToken" and its symbol to "GT".
     * We also set the initial owner to be the person who deploys the contract.
     */
    constructor() ERC20("GameToken", "GT") Ownable(msg.sender) {}

    /**
     * @dev This function creates `amount` new tokens and assigns them to the `to` address.
     * The `onlyOwner` modifier ensures that only the contract's owner can call this function.
     */
    function mint(address to, uint256 amount) public onlyOwner {
        _mint(to, amount); // This is the internal function from ERC20.sol that does the minting.
    }
}

contracts/PlayGame.sol.content:
// SPDX-License-Identifier: MIT
pragma solidity ^0.8.20;

import "@openzeppelin/contracts/token/ERC20/IERC20.sol";
import "@openzeppelin/contracts/utils/ReentrancyGuard.sol";

contract PlayGame is ReentrancyGuard {
    enum MatchStatus { CREATED, STAKED, SETTLED, REFUNDED }

    struct Match {
        address player1;
        address player2;
        uint256 stakeAmount;
        MatchStatus status;
        bool p1_staked;
        bool p2_staked;
        uint256 startTime;
    }

    IERC20 public immutable gameToken;
    address public operator;
    uint256 public constant REFUND_TIMEOUT = 24 hours;

    mapping(bytes32 => Match) public matches;

    event MatchCreated(bytes32 indexed matchId, address indexed p1, address indexed p2, uint256 stake);
    event Staked(bytes32 indexed matchId, address indexed player);
    event Settled(bytes32 indexed matchId, address indexed winner, uint256 prize);
    event Refunded(bytes32 indexed matchId);

    modifier onlyOperator() {
        require(msg.sender == operator, "Caller is not the operator");
        _;
    }

    constructor(address _gameTokenAddress, address _operatorAddress) {
        gameToken = IERC20(_gameTokenAddress);
        operator = _operatorAddress;
    }

    function createMatch(bytes32 matchId, address p1, address p2, uint256 _stake) external onlyOperator {
        require(matches[matchId].stakeAmount == 0, "Match already exists");
        matches[matchId] = Match(p1, p2, _stake, MatchStatus.CREATED, false, false, 0);
        emit MatchCreated(matchId, p1, p2, _stake);
    }

    function stake(bytes32 matchId) external nonReentrant {
        Match storage currentMatch = matches[matchId];
        require(msg.sender == currentMatch.player1 || msg.sender == currentMatch.player2, "Not a player");
        
        if (msg.sender == currentMatch.player1) require(!currentMatch.p1_staked, "Already staked");
        else require(!currentMatch.p2_staked, "Already staked");

        gameToken.transferFrom(msg.sender, address(this), currentMatch.stakeAmount);

        if (msg.sender == currentMatch.player1) currentMatch.p1_staked = true;
        else currentMatch.p2_staked = true;
        
        emit Staked(matchId, msg.sender);

        if (currentMatch.p1_staked && currentMatch.p2_staked) {
            currentMatch.status = MatchStatus.STAKED;
            currentMatch.startTime = block.timestamp;
        }
    }

    function commitResult(bytes32 matchId, address winner) external onlyOperator nonReentrant {
        Match storage currentMatch = matches[matchId];
        require(currentMatch.status == MatchStatus.STAKED, "Match not in STAKED state");
        require(winner == currentMatch.player1 || winner == currentMatch.player2, "Invalid winner");
        
        currentMatch.status = MatchStatus.SETTLED;
        uint256 prize = currentMatch.stakeAmount * 2;
        gameToken.transfer(winner, prize);
        
        emit Settled(matchId, winner, prize);
    }

    function refund(bytes32 matchId) external nonReentrant {
        Match storage currentMatch = matches[matchId];
        require(currentMatch.status == MatchStatus.STAKED, "Match not in STAKED state");
        require(block.timestamp > currentMatch.startTime + REFUND_TIMEOUT, "Timeout not passed");

        currentMatch.status = MatchStatus.REFUNDED;
        
        if (currentMatch.p1_staked) gameToken.transfer(currentMatch.player1, currentMatch.stakeAmount);
        // <<< THE FINAL TYPO 'currentMymatch' IS NOW CORRECTED TO 'currentMatch' HERE
        if (currentMatch.p2_staked) gameToken.transfer(currentMatch.player2, currentMatch.stakeAmount);
        
        emit Refunded(matchId);
    }
}

contracts/TokenStore.sol.content:
// SPDX-License-Identifier: MIT
pragma solidity ^0.8.20;

import "@openzeppelin/contracts/token/ERC20/IERC20.sol";
import "@openzeppelin/contracts/utils/ReentrancyGuard.sol"; // <<< THIS LINE IS NOW CORRECT
import "@openzeppelin/contracts/access/Ownable.sol";
import "./GameToken.sol";

/**
 * @title TokenStore
 * @dev This contract sells GameTokens (GT) to users in exchange for USDT.
 * It is the owner of the GameToken contract to have exclusive minting rights.
 */
contract TokenStore is Ownable, ReentrancyGuard {
    // State variables that are set once and never change. `immutable` saves gas.
    IERC20 public immutable usdt;
    GameToken public immutable gameToken;
    uint256 public immutable gtPerUsdt;

    // An event to log all purchases, making them easy to track on the blockchain.
    event Purchase(address indexed buyer, uint256 usdtAmount, uint256 gtout);

    /**
     * @dev The constructor initializes the contract with the addresses of the USDT and GameToken contracts.
     * @param _usdtAddress The blockchain address of the USDT token contract.
     * @param _gameTokenAddress The address of our GameToken contract we just deployed.
     */
    constructor(address _usdtAddress, address _gameTokenAddress) Ownable(msg.sender) {
        usdt = IERC20(_usdtAddress);
        gameToken = GameToken(_gameTokenAddress);
        // The rate is set to 1e18. This accounts for the decimal difference between USDT (6 decimals)
        // and our GT (18 decimals) to maintain a 1:1 value ratio.
        gtPerUsdt = 1e18;
    }

    /**
     * @dev Allows a user to buy GT by spending their USDT.
     * IMPORTANT: The user must first approve this contract to spend their USDT on their behalf.
     * @param usdtAmount The amount of USDT (with 6 decimals) the user wants to spend.
     */
    function buy(uint256 usdtAmount) external nonReentrant {
        // Calculate the amount of GT to mint. We divide by 1e6 to normalize USDT's 6 decimals.
        uint256 gtout = (usdtAmount * gtPerUsdt) / 1e6;
        require(gtout > 0, "GT amount would be zero");

        // --- Checks-Effects-Interactions Pattern for Security ---

        // 1. Checks: Pull the specified USDT amount from the user to this contract.
        // This call will fail if the user has not approved the spending.
        usdt.transferFrom(msg.sender, address(this), usdtAmount);

        // 2. Effects: If the USDT transfer was successful, mint the calculated GT amount to the user.
        gameToken.mint(msg.sender, gtout);

        // 3. Interactions: Emit an event to log the successful purchase.
        emit Purchase(msg.sender, usdtAmount, gtout);
    }
    
    /**
     * @dev A helper function to be called by the owner after deployment.
     * This transfers the ownership of the GameToken contract to this TokenStore contract.
     * This is the final step to ensure ONLY TokenStore can mint new GT.
     */
    function setGameTokenOwner() external onlyOwner {
        gameToken.transferOwnership(address(this));
    }
}

